/**
 * Shortens a number, by adding a letter represending millions, billions, etc..
 */
export function compact(
  number : number,
  zeroDecimal?: boolean
) : string;

/**
 * Shortens a number, by adding a letter represending millions, billions, etc..
 */
export function compactMini(
  number : number,
  zeroDecimal?: boolean
) : string;
/**
 * Creates a time from seconds to words.
 */
export function compactTime(
  number : number,
) : string;
